This is a modified version from runInGenerator which runs my first example using async/await with babel-node:
// asyncify
[ 'it', 'beforeEach', 'beforeAll', 'afterEach', 'afterAll' ].forEach(fnName => {
  var origFn = global[fnName],
    specTitle, specFn, specTimeout;

  var runSpec = function(specTitle, specFn) {
    return origFn.call(null, specTitle, function(done) {
      specFn().then(done.bind(null, null), done.fail.bind(null));
    });
  };

  var runHook = function(specFn, specTimeout) {
    return origFn.call(null, function(done) {
      specFn().then(done.bind(null, null), done.fail.bind(null));
    }, specTimeout);
  };

  global[fnName] = function() {
    var args = Array.prototype.slice.call(arguments);

    if(fnName === 'it') {
      specTitle = args[0];
      specFn = args[1];

      // check if 'regeneratorRuntime.async' is used
      if(specFn.toString().includes('regeneratorRuntime.async')) {
        return runSpec(specTitle, specFn);
      } else {
        return origFn.call(null, specTitle, specFn);
      }
    }

    specFn = args[0];
    specTimeout = args[1];

    // check if 'regeneratorRuntime.async' is used
    if(specFn.toString().includes('regeneratorRuntime.async')) {
      return runHook(specFn, specTimeout);
    } else {
      return origFn.call(null, specFn, specTimeout);
    }
  };
});

