If someone might be interested, I wrote a monkey patch:
/**
 * Monkey patch fail method to serialize objects
 */
var origFail = jasmine.getEnv().fail;
jasmine.getEnv().fail = function (error) {
  if (typeof error !== 'string' && typeof error === 'object' && !error.message) {
    if (error.toString !== Object.prototype.toString) {
      error = error.toString();
    } else {
      error = JSON.stringify(error);
    }
  }

  return origFail.call(this, error);
};

