Custom equality tester workaround
jasmine.addCustomEqualityTester((first, second) => {
    let test1: boolean = first[0] === second[0]; //test name
    let test2: boolean = first[1].constructor.toString() === second[1].expectedObject.toString();
    return test1 && test2;
});
expect(eventService.broadcast).toHaveBeenCalledWith("myEventName", jasmine.any(Angular2Component));

